    {
      "tags": [
        {
          "name": "/vm/avm"
        }
      ],
      "name": "avm.buildGenesis",
      "summary": "Given a JSON representation of this Virtual Machineâ€™s genesis state, create the byte representation of that state.",
      "params": [
        {
          "name": "alias",
          "required": true,
          "description": "alias X,P or C",
          "schema": {
            "type": "string"
          }
        }
      ],
      "result": {
        "name": "getBlockchainIDResult",
        "schema": {
          "type": "string"
        }
      },
      "examples": [
        {
          "name": "buildGenesisExample",
          "params": [
            {
              "name": "genesisData",
              "value": {
                "genesisData": {
                  "asset1": {
                    "name": "myFixedCapAsset",
                    "symbol": "MFCA",
                    "initialState": {
                      "fixedCap": [
                        {
                          "amount": 100000,
                          "address": "local18jma8ppw3nhx5r4ap8clazz0dps7rv5u00z96u"
                        }
                      ]
                    }
                  },
                  "asset2": {
                    "name": "myVarCapAsset",
                    "symbol": "MVCA",
                    "initialState": {
                      "variableCap": [
                        {
                          "minters": [
                            "local18jma8ppw3nhx5r4ap8clazz0dps7rv5u00z96u"
                          ],
                          "threshold": 1
                        }
                      ]
                    }
                  }
                }
              }
            }
          ],
          "result": {
            "name": "buildGenesisResultExample",
            "value": {
              "bytes": "",
              "encoding": "cb58"
            }
          }
        }
      ]
    },
